'DECLARE',
'brak_imienia EXCEPTION;',
'brak_nazwiska EXCEPTION;',
'brak_wieku EXCEPTION;',
'brak_numeru EXCEPTION;',
'brak_emailu EXCEPTION;',
'brak_ulicy EXCEPTION;',
'BEGIN',
'    "#OWNER#"."KLIENT_OBSLUGA"."DODAJKLIENTA"(',
'        "IMIEU" => :P16_IEU,',
'        "NAZWISKOU" => :P16_ZWISKOU,',
