'    );',
'',
'',
'IF (:P16_IEU IS NULL) OR KLIENT_OBSLUGA.obsluga_liczby(:P16_IEU) THEN',
'    RAISE brak_imienia;',
'ELSIF (:P16_ZWISKOU IS NULL) OR KLIENT_OBSLUGA.obsluga_liczby(:P16_ZWISKOU) THEN',
'    RAISE brak_nazwiska;',
'ELSIF (:P16_EKU IS NULL) OR KLIENT_OBSLUGA.obsluga_litery(:P16_EKU) OR KLIENT_OBSLUGA.obsluga_symbole(:P16_EKU) THEN',
'    RAISE brak_wieku;',
'ELSIF (:P16__TELEFONUU IS NULL) OR KLIENT_OBSLUGA.obsluga_litery(:P16__TELEFONUU) OR KLIENT_OBSLUGA.obsluga_symbole(:P16__TELEFONUU) THEN',
