'ELSIF (:P16_AILU IS NULL) OR NOT KLIENT_OBSLUGA.obsluga_symbole(:P16_AILU) THEN',
'    RAISE brak_emailu;',
'ELSIF :P16_ICAU IS NULL THEN',
'    RAISE brak_ulicy;',
'end if;',
'EXCEPTION ',
'    WHEN brak_imienia THEN',
unistr('    	RAISE_APPLICATION_ERROR(-20001,''Nie wprowadzono imienia lub wprowadzono niedozwolone znaki. Uzupe\0142nij dane.''); '),
