'	WHEN brak_emailu THEN',
unistr('    	RAISE_APPLICATION_ERROR(-20005,''Nie wprowadzono adresu e-mail lub jest nieprawid\0142owy. Uzupe\0142nij dane.''); '),
'	WHEN brak_ulicy THEN',
unistr('    	RAISE_APPLICATION_ERROR(-20006,''Nie wprowadzono ulicy w adresie lub wprowadzono niedozwolone znaki. Uzupe\0142nij dane.''); '),
'    WHEN NO_DATA_FOUND THEN ',
'        RAISE_APPLICATION_ERROR(-20007,''Podany kod pocztowy nie istnieje w tabeli adres.''); ',
'    WHEN OTHERS THEN ',
unistr('        RAISE_APPLICATION_ERROR(-20008,''Wyst\0105pi\0142 b\0142\0105d podczas dodawania klienta''); '),
'END;',
'',
''))
